import React, { useState, useEffect } from 'react';
import { getLeaderboard } from '../utils/contract.ts';

interface LeaderboardPlayer {
  addr: string;
  nickname: string;
  totalWinnings: string;
}

interface LeaderboardProps {
  provider?: any;
}

const Leaderboard = ({ provider }: LeaderboardProps) => {
  const [players, setPlayers] = useState<LeaderboardPlayer[]>([]);
  const [loading, setLoading] = useState(false);

  const fetchLeaderboard = async () => {
    if (!provider) return;

    setLoading(true);
    try {
      const leaderboard = await getLeaderboard(provider);
      setPlayers(leaderboard);
    } catch (error) {
      console.error('Error fetching leaderboard:', error);
      // Показываем пустой лидерборд при ошибке
      setPlayers([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (provider) {
      fetchLeaderboard();
    }
  }, [provider]);

  return (
    <div className="bg-[#232e38] p-4 rounded-lg w-full max-w-xs">
      <h3 className="text-white font-bold text-lg mb-4">Leaderboard</h3>
      {loading ? (
        <div className="text-gray-400 text-center">Loading...</div>
      ) : players.length === 0 ? (
        <div className="text-gray-400 text-center">No players yet</div>
      ) : (
        <div className="space-y-2">
          {players.map((player, index) => (
            <div key={player.addr} className="flex justify-between items-center text-white text-sm">
              <div className="flex items-center gap-2">
                <span className="text-yellow-400 font-bold">#{index + 1}</span>
                <span className="truncate max-w-24">{player.nickname}</span>
              </div>
              <span className="text-green-400 font-bold">{player.totalWinnings} STT</span>
            </div>
          ))}
        </div>
      )}
      <button
        onClick={fetchLeaderboard}
        className="mt-4 w-full bg-blue-600 hover:bg-blue-700 text-white text-sm py-2 rounded"
        disabled={!provider}
      >
        Refresh
      </button>
    </div>
  );
};

export default Leaderboard;
