import React, { useState } from 'react';

const difficulties = [
  { label: 'Easy', value: 'easy' },
  { label: 'Medium', value: 'medium' },
  { label: 'Hard', value: 'hard' },
  { label: 'Expert', value: 'expert' },
  { label: 'Master', value: 'master' },
];

const BetPanel = ({ onConnect, onBet, isConnected }: any) => {
  const [nickname, setNickname] = useState('');
  const [amount, setAmount] = useState('');
  const [difficulty, setDifficulty] = useState('easy');

  return (
    <div className="bg-[#232e38] p-6 rounded-lg w-full max-w-xs flex flex-col gap-4">
      <div className="flex justify-between items-center">
        <button className="bg-[#2c3947] text-white px-4 py-2 rounded-full font-bold">Manual</button>
        <button className="bg-[#2c3947] text-white px-4 py-2 rounded-full font-bold opacity-50 cursor-not-allowed">Auto</button>
      </div>
      <div>
        <label className="block text-gray-300 text-sm mb-1">Nickname</label>
        <input
          className="w-full px-3 py-2 rounded bg-[#1a232b] text-white mb-2"
          placeholder="Enter nickname"
          value={nickname}
          onChange={e => setNickname(e.target.value)}
        />
      </div>
      <div>
        <label className="block text-gray-300 text-sm mb-1">Bet Amount</label>
        <input
          className="w-full px-3 py-2 rounded bg-[#1a232b] text-white"
          placeholder="0.00000000"
          value={amount}
          onChange={e => setAmount(e.target.value)}
          type="number"
          min="0"
        />
      </div>
      <div>
        <label className="block text-gray-300 text-sm mb-1">Difficulty</label>
        <select
          className="w-full px-3 py-2 rounded bg-[#1a232b] text-white"
          value={difficulty}
          onChange={e => setDifficulty(e.target.value)}
        >
          {difficulties.map(d => (
            <option key={d.value} value={d.value}>{d.label}</option>
          ))}
        </select>
      </div>
      <button
        className="w-full bg-green-500 hover:bg-green-600 text-white font-bold py-3 rounded mt-2"
        onClick={() => onBet({ nickname, amount, difficulty })}
        disabled={!isConnected || !nickname || !amount}
      >
        Bet
      </button>
      <button
        className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 rounded"
        onClick={onConnect}
        disabled={isConnected}
      >
        {isConnected ? 'Wallet Connected' : 'Connect Wallet'}
      </button>
    </div>
  );
};

export default BetPanel;
