// Генерирует маршрут: 5 вправо, 5 вниз, 5 влево, 5 вверх
export type PathCell = { x: number; y: number };
export type BoardCell =
  | { type: 'empty' }
  | { type: 'snake' }
  | { type: 'mult'; value: string }
  | { type: 'start' };

export function generatePath(): PathCell[] {
  const path: PathCell[] = [];
  // 5 вправо (y=0, x=0..4)
  for (let x = 0; x < 5; x++) path.push({ x, y: 0 });
  // 5 вниз (x=4, y=1..4)
  for (let y = 1; y < 5; y++) path.push({ x: 4, y });
  // 5 влево (y=4, x=3..0)
  for (let x = 3; x >= 0; x--) path.push({ x, y: 4 });
  // 5 вверх (x=0, y=3..0)
  for (let y = 3; y >= 0; y--) path.push({ x: 0, y });
  return path;
}

function getRandomUnique(arr: number[], count: number): number[] {
  const copy = [...arr];
  for (let i = copy.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [copy[i], copy[j]] = [copy[j], copy[i]];
  }
  return copy.slice(0, count);
}

export function generateBoard(difficulty: 'easy'|'medium'|'hard'|'expert'|'master'): BoardCell[][] {
  const snakesCount = { easy: 4, medium: 7, hard: 10, expert: 13, master: 16 }[difficulty];
  const board: BoardCell[][] = Array.from({ length: 5 }, () => Array(5).fill({ type: 'empty' }));
  const path = generatePath();

  // Случайно выбрать клетки для змей по всему маршруту (кроме старта)
  const snakePositions = getRandomUnique([...Array(path.length - 1).keys()].map(i => i + 1), Math.min(snakesCount, path.length - 1));
  // Индексы змей в path (не включая старт)

  // Расставить змей
  for (const idx of snakePositions) {
    const { x, y } = path[idx];
    board[y][x] = { type: 'snake' };
  }

  // Множители на остальных клетках маршрута (кроме змей и старта)
  const multipliers = ['1.20x','1.30x','2.00x','1.10x','1.01x','1.30x','1.20x','1.10x','1.01x','2.00x','1.30x','1.20x','1.10x','1.01x','1.30x','1.20x','1.10x','1.01x','2.00x','1.30x'];
  let multIdx = 0;
  for (let i = 1; i < path.length; i++) {
    if (!snakePositions.includes(i)) {
      const { x, y } = path[i];
      board[y][x] = { type: 'mult', value: multipliers[multIdx % multipliers.length] };
      multIdx++;
    }
  }
  // Стартовая клетка
  const { x: sx, y: sy } = path[0];
  board[sy][sx] = { type: 'start' };
  return board;
}
