import React from 'react';

const BOARD_SIZE = 5;

const ProgressDots = ({ step }: { step: number }) => (
  <div className="flex justify-center gap-2 mt-4">
    {[0, 1, 2, 3, 4].map(i => (
      <span
        key={i}
        className={`w-3 h-3 rounded-full ${i < step ? 'bg-green-400' : 'bg-gray-500'}`}
      />
    ))}
  </div>
);

interface GameBoardProps {
  step?: number;
  board?: any[][];
  path?: { x: number; y: number }[];
  position?: number;
  lost?: boolean;
  dice?: number | null;
}

const GameBoard = ({ step = 0, board, path, position = 0, lost, dice }: GameBoardProps) => {
  const cells = board || Array.from({ length: BOARD_SIZE }, () => Array(BOARD_SIZE).fill({ type: 'empty' }));
  let playerPos: { x: number; y: number } | null = null;
  if (path && typeof position === 'number' && position < path.length) {
    playerPos = path[position];
  }
  return (
    <div className="flex flex-col items-center">
      <div className="grid grid-cols-5 gap-2 bg-[#232e38] p-6 rounded-lg">
        {cells.flat().map((cell, idx) => {
          const x = idx % BOARD_SIZE;
          const y = Math.floor(idx / BOARD_SIZE);
          const isPlayer = playerPos && playerPos.x === x && playerPos.y === y;
          return (
            <div
              key={idx}
              className={`w-14 h-14 flex items-center justify-center rounded-lg text-white font-bold text-lg
                ${cell.type === 'snake' ? 'bg-red-700' :
                  cell.type === 'mult' ? 'bg-[#2c3947]' :
                  cell.type === 'dice' ? 'bg-[#1a232b] border-2 border-blue-400' :
                  cell.type === 'start' ? 'bg-blue-700' :
                  'bg-[#1a232b]'}
                ${isPlayer ? (lost ? 'ring-4 ring-red-400' : 'ring-4 ring-green-400') : ''}
              `}
            >
              {isPlayer ? (
                dice ? <span className="text-2xl">{dice}</span> : <span className="text-2xl">üé≤</span>
              ) : cell.type === 'snake' ? 'üêç'
                : cell.type === 'mult' ? cell.value
                : cell.type === 'start' ? <span className="text-xs">Start</span>
                : ''}
            </div>
          );
        })}
      </div>
      <ProgressDots step={step} />
    </div>
  );
};

export default GameBoard;
