import React, { useState, useEffect } from 'react';
import BetPanel from './components/BetPanel.tsx';
import GameBoard from './components/GameBoard.tsx';
import Leaderboard from './components/Leaderboard.tsx';
import { generateBoard, generatePath, BoardCell } from './utils/gameLogic.ts';
import { connectWallet, placeBet, cashout } from './utils/contract.ts';

function getRandomInt(min: number, max: number) {
  return Math.floor(Math.random() * (max - min + 1)) + min;
}

function toFixed18(val: number | string) {
  return Number(val).toFixed(18).replace(/0+$/, '').replace(/\.$/, '');
}

const getStoredNickname = (address: string) => {
  if (!address) return '';
  return localStorage.getItem(`nickname_${address}`) || '';
};
const setStoredNickname = (address: string, nickname: string) => {
  if (!address) return;
  localStorage.setItem(`nickname_${address}`, nickname);
};

const App = () => {
  const [isConnected, setIsConnected] = useState(false);
  const [nickname, setNickname] = useState('');
  const [bet, setBet] = useState('');
  const [difficulty, setDifficulty] = useState<'easy'|'medium'|'hard'|'expert'|'master'>('easy');
  const [step, setStep] = useState(0); // сколько бросков
  const [board, setBoard] = useState<BoardCell[][] | null>(null);
  const [path, setPath] = useState<{x:number, y:number}[] | null>(null);
  const [position, setPosition] = useState(0); // индекс в path
  const [profit, setProfit] = useState(0);
  const [gameActive, setGameActive] = useState(false);
  const [lost, setLost] = useState(false);
  const [rolling, setRolling] = useState(false);
  const [dice1, setDice1] = useState<number | null>(null);
  const [dice2, setDice2] = useState<number | null>(null);
  const [message, setMessage] = useState<string | null>(null);
  const [account, setAccount] = useState<string | null>(null);
  const [signer, setSigner] = useState<any>(null);
  const [provider, setProvider] = useState<any>(null);
  const [txStatus, setTxStatus] = useState<string | null>(null);
  const [cashoutPending, setCashoutPending] = useState(false);
  const [canBet, setCanBet] = useState(true); // Можно ли делать ставку
  const [editingNickname, setEditingNickname] = useState(false);
  const [inputNickname, setInputNickname] = useState('');

  // При подключении кошелька — подгружаем никнейм
  useEffect(() => {
    if (account) {
      const stored = getStoredNickname(account);
      setNickname(stored);
      setInputNickname(stored);
      setEditingNickname(!stored); // если нет ника — сразу редактировать
    }
  }, [account]);

  // Смена никнейма
  const handleSaveNickname = () => {
    setNickname(inputNickname);
    setStoredNickname(account, inputNickname);
    setEditingNickname(false);
    // TODO: здесь можно вызвать функцию смены ника в контракте
  };

  const handleConnect = async () => {
    try {
      const { provider, signer } = await connectWallet();
      const address = await signer.getAddress();
      setSigner(signer);
      setProvider(provider);
      setAccount(address);
      setIsConnected(true);
    } catch (e) {
      setTxStatus('Ошибка подключения к MetaMask');
    }
  };

  const handleBet = async ({ amount, difficulty }: any) => {
    setBet(amount);
    setDifficulty(difficulty);
    setTxStatus('Отправка транзакции...');
    try {
      await placeBet(signer, nickname, amount); // nickname из useState
      setTxStatus('Ставка принята!');
      const b = generateBoard(difficulty);
      const p = generatePath();
      setBoard(b);
      setPath(p);
      setPosition(0);
      setStep(0);
      setProfit(0); // сбрасываем profit при новой ставке
      setGameActive(true);
      setLost(false);
      setDice1(null);
      setDice2(null);
      setMessage(null);
      setCanBet(false); // После ставки нельзя делать новую, пока не закончится раунд
    } catch (e) {
      setTxStatus('Ошибка транзакции');
    }
  };

  // Анимация движения по маршруту
  const animateMove = async (from: number, move: number, path: {x:number, y:number}[], board: BoardCell[][]) => {
    let lose = false;
    let msg = '';
    let finalPos = from;
    let totalMult = 1; // стартовый множитель
    let visited = new Set();
    for (let i = 1; i <= move; i++) {
      const pos = (from + i) % path.length;
      setPosition(pos);
      finalPos = pos;
      visited.add(pos);
      console.log(`[ANIMATE] Step: ${i}, Cell:`, path[pos]);
      await new Promise(res => setTimeout(res, 400));
    }
    for (let pos of visited) {
      const { x, y } = path[pos];
      const cell = board[y][x];
      if (cell.type === 'mult') {
        const mult = parseFloat(cell.value.replace('x', ''));
        totalMult += (mult - 1);
      }
    }
    // Проверяем только конечную клетку на змею
    const { x, y } = path[finalPos];
    const cell = board[y][x];
    let newProfit = 0;
    if (cell.type === 'snake') {
      lose = true;
      msg = 'Вы попали на змею! Ставка проиграна.';
      setLost(true);
      setGameActive(false);
      setProfit(0);
      setMessage(msg);
    } else {
      newProfit = Number(bet) * totalMult;
      setProfit(newProfit); // profit только за текущий бросок
      setMessage('');
    }
    console.log('BET:', bet, 'totalMult:', totalMult, 'newProfit:', newProfit, 'profit:', profit);
    return { lose, newProfit, msg };
  };

  const handleRoll = async () => {
    if (!gameActive || !path || !board || rolling) return;
    setRolling(true);
    // Анимация броска двух кубиков
    let final1 = getRandomInt(1, 6);
    let final2 = getRandomInt(1, 6);
    for (let i = 0; i < 10; i++) {
      setDice1(getRandomInt(1, 6));
      setDice2(getRandomInt(1, 6));
      await new Promise(res => setTimeout(res, 60));
    }
    setDice1(final1);
    setDice2(final2);
    // Суммируем значения кубиков
    let move = final1 + final2;
    console.log('ROLL:', { final1, final2, move, position });
    console.log('Moving from', position, 'by', move, 'steps');
    console.log('path.length:', path.length, 'position:', position, 'move:', move);
    const { lose, newProfit, msg } = await animateMove(position, move, path, board);
    console.log('AFTER ROLL: profit:', profit, 'newProfit:', newProfit);
    setStep(s => s + 1);
    setLost(lose);
    setGameActive(!lose && step + 1 < 5);
    setMessage(lose ? msg : (step + 1 >= 5 ? `Раунд завершён! Ваш выигрыш: ${newProfit.toFixed(4)} STT` : `Выпало: ${final1} + ${final2} = ${move}`));
    setRolling(false);
  };

  const handleCashout = async () => {
    setGameActive(false);
    setTxStatus('Выплата...');
    setCashoutPending(true);
    try {
      const roundedProfit = Number(profit).toFixed(8); // <= исправление!
      const tx = await cashout(signer, roundedProfit);
      setTxStatus('Ожидание подтверждения транзакции...');
      await tx.wait();
      setTxStatus('Выплата успешно отправлена!');
      setMessage(`Вы забрали: ${roundedProfit} STT. Сделайте новую ставку, чтобы продолжить игру.`);
      setCanBet(true);
    } catch (e: any) {
      setTxStatus('Ошибка выплаты: ' + (e?.reason || e?.message || ''));
    } finally {
      setCashoutPending(false);
    }
  };

  const handleRestart = () => {
    setBoard(null);
    setPath(null);
    setPosition(0);
    setStep(0);
    setProfit(0); // сбрасываем profit при рестарте
    setGameActive(false);
    setLost(false);
    setDice1(null);
    setDice2(null);
    setMessage(null);
  };

  return (
    <div className="min-h-screen bg-[#1a232b] flex items-center justify-center">
      <div className="flex gap-8 w-full max-w-5xl p-8">
        <div className="flex flex-col gap-6">
          {/* Никнейм и кнопка смены */}
          {editingNickname ? (
            <div className="mb-2 flex gap-2 items-center">
              <input
                className="px-3 py-2 rounded bg-[#1a232b] text-white"
                placeholder="Введите никнейм"
                value={inputNickname}
                onChange={e => setInputNickname(e.target.value)}
                maxLength={20}
              />
              <button
                className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded"
                onClick={handleSaveNickname}
                disabled={!inputNickname}
              >
                Сохранить
              </button>
            </div>
          ) : (
            <div className="mb-2 flex gap-2 items-center">
              <span className="text-white font-bold">{nickname || 'Без ника'}</span>
              <button
                className="bg-blue-600 hover:bg-blue-700 text-white text-xs py-1 px-3 rounded"
                onClick={() => setEditingNickname(true)}
              >
                Сменить никнейм
              </button>
            </div>
          )}
          <BetPanel
            onConnect={handleConnect}
            onBet={handleBet}
            isConnected={isConnected}
          />
          <Leaderboard provider={provider} />
        </div>
        <div className="flex-1 flex flex-col items-center justify-center">
          {account && <div className="mb-2 text-xs text-gray-400">{account}</div>}
          {txStatus && <div className="mb-2 text-sm text-yellow-300">{txStatus}</div>}
          <GameBoard
            step={step}
            board={board}
            path={path}
            position={position}
            lost={lost}
            dice1={dice1}
            dice2={dice2}
          />
          {gameActive && (
            <div className="flex gap-4 mt-6">
              <button
                className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-6 rounded"
                onClick={handleRoll}
                disabled={step >= 5 || lost || rolling}
              >
                {rolling ? 'Rolling...' : 'Roll'}
              </button>
              <button
                className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-6 rounded"
                onClick={handleCashout}
                disabled={step === 0 || lost || rolling || cashoutPending || canBet}
              >
                Cashout
              </button>
            </div>
          )}
          {message && (
            <div className={`mt-4 text-lg font-bold ${lost ? 'text-red-400' : 'text-green-300'}`}>{message}</div>
          )}
          {!gameActive && (step > 0 || lost) && (
            <button
              className="mt-6 bg-gray-700 hover:bg-gray-600 text-white font-bold py-2 px-6 rounded"
              onClick={handleRestart}
            >
              Начать заново
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
